$(function(){$('a[href^="#"]').on("click",function(d){var e=$($(this).attr("href"));if(e.length){d.preventDefault();$("html, body").animate({scrollTop:(e.offset().top-90)},"fast","swing")}});var c=0;var a=3;$(function b(){c++;if(c>=a){c=0}$(".carousel").each(function(){var d=$(this);d.fadeOut(1000,function(){d.attr("src","img/menuphoto/"+d.attr("id")+"-"+(c+1)+".jpg");d.fadeIn(1000,false)})});setTimeout(b,5000)})});