$(function(){$('a[href^="#"]').on("click",function(d){var e=$($(this).attr("href"));if(e.length){d.preventDefault();$("html, body").animate({scrollTop:(e.offset().top-90)},"fast","swing")}});var a=0;var b=3;$(function c(){a++;if(a>=b){a=0}$(".carousel").each(function(){var d=$(this);d.fadeOut(1000,function(){d.css("background-image","url(img/menuphoto/"+d.attr("id")+"-"+(a+1)+".jpg)");d.fadeIn(1000,false)})});setTimeout(c,5000)})});